option.value:
// 错误示例：Arrays.asList() 返回的列表不支持 add()
List<String> payloads = Arrays.asList("initItem1", "initItem2");

// 正确做法：使用可变的 ArrayList
List<String> payloads = new ArrayList<>(Arrays.asList("initItem1", "initItem2"));
payloads.add("druid.html"); // 现在可以正常调用 add

public class MyClass {
    private List<String> payloads = new ArrayList<>();

    public void addPayload(String item) {
        payloads.add(item);
    }
}

import java.util.ArrayList;
import java.util.List;

public class MyScanCheck {
    private List<String> payloads = new ArrayList<>(); // 正确初始化

    public void initPayloads() {
        payloads.add("druid.html"); // 正常调用 add
        payloads.add("test.jsp");
    }
}

